const url = $input.first().json.event_link

try {
  if (typeof url !== "string") {
    return [{ valid: false, reason: "Invalid URL" }];
  }

  // Remove everything after "?"
  const cleaned = url.split("?")[0];

  const blockedDomains = ["youtube.com", "youtu.be", "tiktok.com"];
  const lower = cleaned.toLowerCase();

  if (blockedDomains.some(d => lower.includes(d))) {
    return [{ valid: false, reason: "Disallowed domain" }];
  }

  return [{ valid: true, url: cleaned }];
} catch {
  return [{ valid: false, reason: "Invalid URL" }];
}

return items.map(item => {
  const raw = $('Scrape a url and get its content').first().json.data.json.location;

  if (!raw || typeof raw !== "string") {
    return { json: { location: null } };
  }

  // Split by commas
  const parts = raw.split(",").map(p => p.trim());
  const first = parts[0];

  // Detect a street pattern: ends with a number (e.g. "Pacellistra√üe 2")
  const looksLikeStreet = /\b\d+$/.test(first);

  if (!looksLikeStreet) {
    // First part is a real venue name
    return {
      json: {
        location: first
      }
    };
  }

  // Otherwise, it's a street ‚Üí no venue name found
  return {
    json: {
      location: raw
    }
  };
});

return items.map(item => {
  const e = item.json;

  // --- Safe fields
  const name = e.name ?? "Unnamed Hackathon";
  const description = e.description ?? "No description available.";
  const startDate = e.start_date ?? "Date TBA";
  const startTime = e.start_time ?? "";
  const endDate = e.end_date ?? "";
  const endTime = e.end_time ?? "";
  const location = e.location ?? "Location TBA";
  const organisers = e.organisers ?? "Organisers TBA";
  const link = e.link ?? "#";
  const signupDeadline = e.signup_deadline ?? null;
  const prizes = e.prizes;

  // --- Helpers
  function formatDate(d) {
    try {
      return new Date(d).toLocaleDateString("en-US", {
        month: "short",
        day: "numeric"
      });
    } catch {
      return d;
    }
  }

  const sd = formatDate(startDate);
  const ed = endDate ? formatDate(endDate) : null;

  const st = startTime ? startTime.slice(0, 5) : "";
  const et = endTime ? endTime.slice(0, 5) : "";

  // --- Date line
  let dateLine = `${sd}`;
  if (st) dateLine += `, ${st}`;
  if (ed) dateLine += `  ‚Üí  ${ed}`;
  if (et) dateLine += `, ${et}`;

  // --- Optional lines
  const prizeLine = prizes
    ? `üèÜ Prizes: ${prizes}`
    : `üèÜ Prizes: To be revealed...`;

  const deadlineLine = signupDeadline
    ? `üóìÔ∏è Deadline: ${formatDate(signupDeadline)}`
    : null;

  // --- EXACT WhatsApp structure
  let final = `NEW HACKATHON ALERT üî•üöÄ\n\n`;
  final += `*üß© ${name}*\n`;
  final += `üìÖ ${dateLine}\n`;
  final += `üìç ${location}\n`;
  final += `üë• ${organisers}\n\n`;
  final += `‚ú® ${description}\n\n`;
  final += `${prizeLine}\n`;
  if (deadlineLine) final += `${deadlineLine}\n\n`;
  else final += `\n`; // preserve spacing if no deadline
  final += `üîó Apply: ${link}\n\n`;
  final += `‚Äî Share this with Everyone you know! üôåü©µ`

  return {
    json: {
      formatted: final
    }
  };
});

