={
  "url": "{{ $('Validate URL').item.json.url }}",
  "formats": [
    {
      "type": "json",
      "prompt": "Extract hackathon information from the webpage and return JSON strictly matching the provided schema. Rules: - All fields except ‚Äúprizes‚Äù and ‚Äúsignup_deadline‚Äù must be extracted and cannot be null. - If ‚Äúprizes‚Äù or ‚Äúsignup_deadline‚Äù are not present on the page, return null. - Convert dates to ISO format (YYYY-MM-DD) whenever possible. - Convert times to 24h format (HH:MM). - Do not hallucinate or infer details ‚Äî only return what appears explicitly on the page. - ‚Äúdescription‚Äù must be a clean 1‚Äì3 sentence summary without markdown. - ‚Äúorganisers‚Äù must list companies, organizations, or institutions hosting the hackathon. - ‚Äúsignup_link‚Äù must be the main Apply/Register button (CTA) on the page. - Return only the JSON object defined by the schema.",
      "schema": {
        "type": "object",
        "properties": {
          "name": { 
            "type": "string",
            "description": "Official name of the hackathon"
          },
          "description": { 
            "type": "string",
            "description": "Short, clean description of the hackathon with no markdown"
          },
          "start_date": { 
            "type": "string",
            "description": "Start date of the hackathon in ISO format (YYYY-MM-DD) if possible"
          },
          "start_time": { 
            "type": "string",
            "description": "Start time in 24h format (HH:MM)"
          },
          "end_date": { 
            "type": "string",
            "description": "End date of the hackathon in ISO format (YYYY-MM-DD) if possible"
          },
          "end_time": { 
            "type": "string",
            "description": "End time in 24h format (HH:MM)"
          },
          "location": {
            "type": "string",
            "description": "Event location, city or venue name if available"
          },
          "organisers": { 
            "type": "string",
            "description": "Organizer(s) or host(s) of the hackathon"
          },
          "signup_link": { 
            "type": "string",
            "description": "Registration or application link for the hackathon"
          },
          "prizes": { 
            "type": ["string","null"],
            "description": "What winners receive; null if not provided"
          },
          "signup_deadline": { 
            "type": ["string","null"],
            "description": "Signup or registration deadline in ISO format; null if not stated"
          },
          "link": {
            "type": "string",
            "description": "The hackathon page URL being scraped"
          }
        },
        "required": [
          "name",
          "description",
          "start_date",
          "start_time",
          "end_date",
          "end_time",
          "location",
          "organisers",
          "signup_link",
          "link"
        ]
      }
    }
  ]
}
